// Get a random tile from a specific set
export function getTileFromSet(biome, setIndex = 0, rng = Math.random) {
    const sets = UO_TILE_SETS[biome];
    if (!sets || sets.length === 0) return null;
    const set = sets[Math.min(setIndex, sets.length - 1)];
    return set[Math.floor(rng() * set.length)];
}